mapper.select:
select c.fid as id, c.fnumber as num,c.flongnumber as longNumber,c.fname_l2 as title, c.FAssigned as assigned,c.FLevel,c.FDescription_l2 as description, c.FCurProject as projectName,c.FFullOrgUnit as fullOrgUnit, b.FNAME_L2 as orgName,e.ftype as controlType from T_FDC_CostAccount c left join T_ORG_BaseUnit b on b.fid=c.FFULLORGUNIT left join T_CON_MarketProjectCostEntry e on e.FCOSTACCOUNTID=c.FID
e.FHEADID=#{marketId}
and c.fid not in
#{item}
and e.ftype=#{controlType}

select c.fid as id, c.fnumber as num,c.flongnumber as longNumber,c.fname_l2 as title, c.FAssigned as assigned,c.FLevel,c.FDescription_l2 as description, c.FCurProject as projectName,c.FFullOrgUnit as fullOrgUnit,mp.FISSUB as isSub, b.FNAME_L2 as orgName,e.ftype as controlType,e.FAMOUNT as mpAmount from T_FDC_CostAccount c left join T_ORG_BaseUnit b on b.fid=c.FFULLORGUNIT left join T_CON_MarketProjectCostEntry e on e.FCOSTACCOUNTID=c.FID left join T_CON_MarketProject mp on mp.fid=e.FHEADID
e.FHEADID=#{marketId}
and e.ftype=#{controlType}
