mapper.select:
select m.fid as id,m.FNAME_L2 as title,m.FDESCRIPTION_L2 as description,m.fnumber as num, m.FLONGNUMBER as longNumber,m.flevel,m.FPartCostCenterID as costCenterId,m.FDESCRIPTION_L2 as description, m.FISLEAF as isLeaf,m.FSIMPLENAME as simpleName,fi.FISBIZUNIT as isCompany,c.FISBIZUNIT as isSTCost from T_ORG_BASEUNIT m left join T_ORG_OUPartFI fi on fi.FID=m.FPARTFIID left join T_ORG_OUPartCostCenter c on c.fid=m.FPartCostCenterID
m.FIsOUSealUp=0 and m.FISADMINORGUNIT=1 and m.FParentID=#{id,jdbcType=VARCHAR}
order by m.flongnumber

select fid as id from T_ORG_BASEUNIT
FIsOUSealUp=0 and FISADMINORGUNIT=1 and FParentID=#{id,jdbcType=VARCHAR}
order by flongnumber

select fid as id from T_ORG_BASEUNIT
FISADMINORGUNIT=1 and FIsOUSealUp=0 and FIsCostOrgUnit=1 and FParentID=#{id,jdbcType=VARCHAR}
order by flongnumber

select sum(m.famount)
from T_FNC_projectMonthPGDateEntry m
left join T_FNC_ProjectMonthPlanGEntry e on e.fid=m.FHeadEntryId
left join T_FNC_ProjectMonthPlangather er on er.fid=e.fheadid
where
m.fyear=#{year}
and m.fmonth=#{month}
and er.FCurProjectId=#{projectId}
and e.contractBillId is null
and er.FIsLatest=1

select sum(FORIGINALAMOUNT)
from T_CON_CONTRACTWITHOUTTEXT

      #{monthFirst} <=  FBOOKEDDATE
    and FBOOKEDDATE    < #{monthEnd}

    and FSTATE in('1SAVED','2SUBMITTED','3AUDITTING','4AUDITTED')
    and FCURPROJECTID=#{projectId}

        and fid !=#{id}

select m.fid as id,m.FNAME_L2 as title,m.FDESCRIPTION_L2 as description,m.fnumber as num, m.FLONGNUMBER as longNumber,m.flevel,m.FPartCostCenterID as costCenterId,m.FISLEAF as isLeaf, m.FSIMPLENAME as simpleName,fi.FISBIZUNIT as isCompany,c.FISBIZUNIT as isSTCost from T_ORG_BASEUNIT m left join T_ORG_OUPartFI fi on fi.FID=m.FPARTFIID left join T_ORG_OUPartCostCenter c on c.fid=m.FPartCostCenterID
m.FIsOUSealUp = 0 and m.fid=#{id}
order by m.FLONGNUMBER
