mapper.select:
select fid as id,FNAME_L2 as title,FDESCRIPTION_L2 as description,fnumber as num, FLONGNUMBER as longNumber,flevel,FPartCostCenterID as costCenterId,FDESCRIPTION_L2 as description, FISLEAF as isLeaf,FSIMPLENAME as simpleName from T_ORG_BASEUNIT
FIsOUSealUp=0 and FISADMINORGUNIT=1 and FParentID=#{id,jdbcType=VARCHAR}
order by flongnumber

select fid as id from T_ORG_BASEUNIT
FIsOUSealUp=0 and FISADMINORGUNIT=1 and FParentID=#{id,jdbcType=VARCHAR}
order by flongnumber

select fid as id from T_ORG_BASEUNIT
FISADMINORGUNIT=1 and FIsOUSealUp=0 and FIsCostOrgUnit=1 and FParentID=#{id,jdbcType=VARCHAR}
order by flongnumber

select m.fid as id,m.FNAME_L2 as title,m.FNUMBER as num,m.FLONGNUMBER as longNumber, m.FSIMPLENAME as simpleName,m.FISLEAF as isLeaf,m.FDESCRIPTION_L2 as description, fi.FISBIZUNIT as isCompany from T_ORG_BASEUNIT m left join T_ORG_OUPartFI fi on fi.FID=m.FPARTFIID
m.FIsOUSealUp=0 and exists (select fi.fid from T_ORG_OUPartFI fi where fi.fid=m.FPartFIID) and m.FPARENTID=#{id}
and fi.FISBIZUNIT =#{isCompany}
order by m.flongnumber
